import { useEffect, useState } from "react"
import { collection, getDocs } from "firebase/firestore";
import { db } from "./config";
import { async } from "@firebase/util";
const AppLeerDatos = () =>{
    const [persona, setPersona] = useState(null);
    
    const leerDatos = async()=>{
        const resultado = await getDocs(collection(db, "users"));
        return resultado;
    }

    useEffect(()=>{
        verPersonas();
    });

    const verPersonas = async()=>{
        const p = await leerDatos();
        setPersona(p.docs);
    }

    return(
        <div>
            <table>
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>User name</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                        {persona && persona.map(p=>(
                            <tr>
                                <td>{p.data().name}</td>
                                <td>{p.data().username}</td>
                            </tr>
                        ))}
                </tbody>
            </table>
        </div>
    )
}
export default AppLeerDatos;
