import { useState } from "react";
import { AgregarDatos } from "./Api";
const AppInsertarDatos = () =>{
  const initialForm = {name: '', username: ''};
  const [personaDatos, setPersonaDatos] = useState(initialForm);

  const handleInputChange = (event) =>{
    const {name, value} = event.target;
    setPersonaDatos({...personaDatos, [name]:value})
  }

  const savePersona = async() =>{
    await AgregarDatos(personaDatos);
    setPersonaDatos(initialForm);
  }
    
  return(
        <div className="container">
          <h2>Agregar usuarios</h2>
            <label>Nombre</label>
            <input type="text" name="name" onChange={handleInputChange}/>
            <label>Username</label>
            <input type="text" name="username" onChange={handleInputChange}/>
            <button onClick={savePersona}>Agregar nuevo usuario</button>
        </div>
  )
}

export default AppInsertarDatos;
