import { addDoc, collection } from "firebase/firestore";
import { useEffect, useState } from "react";
import { db } from "./config";
const AppInsertarDatos = () =>{
  const savePersonaDatos = (name, username) =>{
    addDoc(collection(db, 'users'), {name, username})
  }
  
  const [personaDatos, setPersonaDatos] = useState(null);

    const savePersona = () =>{
      savePersonaDatos(personaDatos);
    }
    
    return(
        <div className="container">
          <h2>Agregar usuarios</h2>
          <form>
            <label>Nombre</label>
            <input type="text" name="name" onChange={e=> setPersonaDatos(e.target.value)}/>

            <label>Username</label>
            <input type="text" name="username" onChange={e=> setPersonaDatos(e.target.value)}/>

            <button onClick={savePersona}>Agregar nuevo usuario</button>
          </form>
        </div>
    )
}

export default AppInsertarDatos;
